        negateLoggedIn();
    }



    return (
        <form className="login">
            <h1>Login</h1>
            <form onSubmit={onSubmit}>
                <label>Email:</label>
                <input type="text"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)} />
                <label>Password:</label>
                <input type="text"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)} />
                <button className="login-btn">Login</button>
            </form>
        </form>
    );
};
